import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Leftcolumn from './components/Leftcolumn';
import Rightcolumn from './components/Rightcolumn';
import { useMediaQuery, ThemeProvider, createTheme } from '@mui/material';
import { Stack } from '@mui/system';



const App = () => {
  const theme = createTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('sm'));


  return (
    <>
    <ThemeProvider theme={theme}>
      <Box sx={{ height: "100vh", display: 'flex', flexDirection: isSmallScreen ? 'column' : 'row' }}>
        <Grid container spacing={0} direction={isSmallScreen ? 'column' : 'row'}>
          <Grid item sx={{ width: { lg: "256px", xs: '100%' }, backgroundColor: "#F3F4F6" }}>
            <Leftcolumn />
          </Grid>
          <Grid item sx={{ flexGrow: 1, height: '100%', backgroundColor: "#FFFFFF" }}>
            <Rightcolumn />
          </Grid>
        </Grid>
      </Box>
    </ThemeProvider>



    </>

  )
}

export default App
