import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Leftcolumn from './components/Leftcolumn';
import Rightcolumn from './components/Rightcolumn';
import { useMediaQuery, ThemeProvider, createTheme } from '@mui/material';
import { Stack } from '@mui/system';



const App = () => {
  const theme = createTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('sm'));


  return (
    <>
      <Stack sx={{ height:"100vh",flexGrow: 1,margin:"auto", display: 'flex', direction: isSmallScreen ? "column" : "row" }}>
        <Leftcolumn/>
        <Rightcolumn/>
      </Stack>

        <Grid container spacing={0} sx={{ height:"100vh",flexGrow: 1,margin:"auto", display: 'flex', direction: isSmallScreen ? "column" : "row" }} >
          <Grid item sx={{ width: { lg: 256}, backgroundColor:"#F3F4F6" }} >
            <Leftcolumn/>
          </Grid>
          <Grid  item sx={{ flexGrow: 1,height: '100%',backgroundColor:"#FFFFFF" }} >
            <h1>heeloo</h1>
           <Rightcolumn/> 
          </Grid>
        </Grid>

    </>

  )
}

export default App

