import React from 'react'
import { storage } from '../firebase.config'
import { ref, uploadBytes, getDownloadURL, listAll, deleteObject } from 'firebase/storage'

const deleteUserAPI = async (id,imagepath) => {
  const res= await fetch ( `${process.env.REACT_APP_API_ADDRESS_DEV}?id=${id}`, {
   method:'DELETE',
   headers:  {
     'Content-Type': 'application/json',
   }
  })
  return await res.json();
 }

 const deleteImageInStorage = async (imagepath)=> {
   const imageRef = ref(storage, imagepath)
   const result = await deleteObject(imageRef)
   console.log(result)
 }

 const deleteUser = async ({ id, imagepath }) => {
   await deleteImageInStorage(imagepath);
   const res = await deleteUserAPI(id);
   console.log(res)
 }

 export default deleteUser
