import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Leftcolumn from './components/Leftcolumn';
import Rightcolumn from './components/Rightcolumn';
import { useMediaQuery, ThemeProvider, createTheme } from '@mui/material';


const theme = createTheme();
const App = () => {

  const isSmallScreen = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <>
      <Box sx={{ height:"100vh",flexGrow: 1,margin:"auto",flexDirection: isSmallScreen ? "column" : "row", display: 'flex' }}>
        <Grid container spacing={0} >
          <Grid item sx={{ width: { lg: 256, backgroundColor:"#F3F4F6" } }} >
            <Leftcolumn/>
          </Grid>
          <Grid  item sx={{ flexGrow: 1,height: '100%',backgroundColor:"#FFFFFF" }} >
           <Rightcolumn/> 
          </Grid>
        </Grid>
      </Box>
    </>

  )
}

export default App
