import React from 'react'
import {useState} from 'react'
import Button from '@mui/material/Button';
import {addUser} from '../api/api'
import { nanoid } from 'nanoid';
import { Stack } from '@mui/system';
import { Typography,TextField } from '@mui/material';

const AddUser = () => {

  const [userInfo,setUserInfo] = useState({
    name:'',
    email:'',
    phone:''
  })

  const newUser = {
    id: nanoid(),
    name:userInfo.name,
    phone:userInfo.phone,
    email:userInfo.email 
   };
 const changeUserInfo = (e) => { 
  const {name,value} = e.target
  setUserInfo({...userInfo,[name]:value})
  }

  const validateUserInput = () => {
    const {name,email,phone} = userInfo
    if(!name || email || phone) {
      return alert('Please Type User Info')
    }
    addUser(newUser);
  }


  return (
    <>
      <Stack>
        <Typography sx={{color:'#344054',fontSize:'13px'}} mb={'5px'}>Name</Typography>
        <TextField size='small' sx = {{width:'100%',height:'34px',mb:'20px'}}/>
        <Typography sx={{color:'#344054',fontSize:'13px'}} mb={'5px'}>Email</Typography>
        <TextField size='small' sx = {{width:'100%',height:'34px',mb:'20px'}}/>
        <Typography sx={{color:'#344054',fontSize:'13px'}} mb={'5px'}>phone</Typography>
        <TextField size='small' sx = {{width:'100%',height:'34px',mb:'20px'}}/>
        <div>Name</div>
        <input type='text' name="name" value={userInfo.name} onChange={changeUserInfo}/>
        <div>Email</div>
        <input type='text' name="Email" value={userInfo.email} onChange={changeUserInfo}/>
        <div>Phone</div>
        <input type='text' name="Phone" value={userInfo.phone} onChange={changeUserInfo}/>
        <Stack direction="row">
          <Button variant="contained" onClick={()=>{validateUserInput()}}>Add User</Button>
          <Button variant="outlined" onClick={()=>{validateUserInput()}}>Cancel</Button>

        </Stack>

      </Stack>

    </>
  )
}

export default AddUser
