import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import AddIcon from '@mui/icons-material/Add';
import { borderRadius } from '@mui/system';
import AddUser from './AddUser';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '1px solid #E5E7EB',
  borderRadius:'5px',
  boxShadow: 24,
  p: 4,
};

export default function InputModal() {
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  return (
    <div>
    <Button variant="outlined" onClick={handleOpen} startIcon={<AddIcon />} sx={{borderRadius:"50px",borderWidth:"2px", borderColor: '#436AE9',color:'#436AE9','&:hover': {
          borderColor: '#436AE9', // Keep the same color on hover
          backgroundColor: '#436AE9',
          color:'#FFFFFF',
          borderWidth: '2px', // Ensure the border width stays the same on hover
        }, }}>
        Add Users
      </Button>



      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography>Add a New User</Typography>
          <AddUser/>

        </Box>
      </Modal>
    </div>
  );
}
