import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import Leftcolumn from './components/Leftcolumn';
import Rightcolumn from './components/Rightcolumn';
import { useMediaQuery, ThemeProvider, createTheme } from '@mui/material';
import { Stack } from '@mui/system';



const App = () => {
  const theme = createTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down('md'));


  return (
    <>
    <ThemeProvider theme={theme}>
    <Box sx={{ height:"100vh",flexGrow: 1,margin:"auto", display: 'flex', flexDirection: isSmallScreen ? "column" : "row" }}>
        <Grid container spacing={0} >
            <Grid item sx={{ width:"256px", backgroundColor:"#F3F4F6" }} >
              <Leftcolumn/>
            </Grid>
            <Grid  item sx={{ height: '100%',backgroundColor:"#FFFFFF" }} >
              <Rightcolumn/> 
            </Grid>
          </Grid>
       </Box>
    </ThemeProvider>



    </>

  )
}

export default App
