import React from 'react'
import { storage } from '../firebase.config'
import { ref, uploadBytes, getDownloadURL, listAll, deleteObject } from 'firebase/storage'

const deleteUserAPI = async (id) => {
  const res= await fetch ( `${process.env.REACT_APP_API_ADDRESS_DEV}?id=${id}`, {
   method:'DELETE',
   headers:  {
     'Content-Type': 'application/json',
   }
  })
  return await res.json();
 }

 const deleteImageInStorage = async (imgpath)=> {
   const imageRef = ref(storage, imgpath)
   const result = await deleteObject(imageRef)
   console.log(result)
 }

 const deleteUser = async ( {selecteduser} ) => {
   await deleteImageInStorage(selecteduser.imagePath);

   const res = await deleteUserAPI(selecteduser.id);
  }

 export default deleteUser
