import React, { useEffect, useState } from 'react'
import Grid from '@mui/material/Grid';
import PortfolioBox from './PortfolioBox';

const ShowUser = () => {
  const [userList,setUserList] = useState({
    user:[],
    loading:false
  })

  useEffect(()=>{
    const getUsersAPI = async () => {
      const res = await fetch(`${process.env.REACT_APP_API_ADDRESS_DEV}`,{
        method: 'GET',
        headers: {
          'Content-Type' : 'application/json'
        }
      });
      const {result} = await res.json();
      setUserList({...userList, user:result, loading:false})
    }
    getUsersAPI()
  },[])

  if(userList.loading) {
    return <div>Loading...</div>
  }
  <>
    {userList.user.map((userinfo)=>{
      return (
        <>
           <Grid item lg={2.4} >
            <PortfolioBox id ={userinfo.id} img={userinfo.imageUrl} name={userinfo.name} email={userinfo.email} phone={userinfo.phone}/>
           </Grid>
        </>
      )
    })}
  </>


}

export default ShowUser
